parameters.jsCode:
// Get the AI response and clean it for Slack
const aiResponse = $('AI Agent').first().json.output;

// Clean the response: remove ALL newlines and escape quotes
const cleanedResponse = aiResponse
  .replace(/\n/g, ' ')      // Replace ALL newlines with spaces
  .replace(/"/g, '\\"')     // Escape quotes
  .substring(0, 2800);      // Limit length to avoid Slack limits

return [{
  json: {
    cleanedAiResponse: cleanedResponse
  }
}];

